'use strict';

// MODULES //

var tape = require( 'tape' );
var join = require( 'path' ).join;
var cwd = require( '@stdlib/process/cwd' );
var unlink = require( '@stdlib/fs/unlink' ).sync;
var rename = require( '@stdlib/fs/rename' ).sync;
var readFile = require( '@stdlib/fs/read-file' ).sync;
var noop = require( '@stdlib/utils/noop' );
var bundle = require( './../lib' );


// FIXTURES //

var FIXTURES = join( __dirname, 'fixtures' );
var EXPECTED = readFile( join( FIXTURES, 'expected.txt' ) );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof bundle, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if the first argument is not a string primitive', function test( t ) {
	var values;
	var i;
	values = [
		5,
		NaN,
		null,
		void 0,
		true,
		{},
		[],
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws a type error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			bundle( value, noop );
		};
	}
});

tape( 'the function throws an error if provided a callback argument which is not a function', function test( t ) {
	var values;
	var i;
	values = [
		5,
		'abc',
		NaN,
		null,
		void 0,
		true,
		{},
		[]
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws a type error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			bundle( cwd(), value );
		};
	}
});

tape( 'the function bundles benchmarks into a single file using `browserify`', function test( t ) {
	var oldPath = join( FIXTURES, 'index.js' );
	var newPath = join( FIXTURES, 'benchmark.js' );
	rename( oldPath, newPath );
	bundle( FIXTURES, onRead );

	function onRead( error, output ) {
		if ( error ) {
			t.ok( false, 'did not expect an error' );
		}
		t.deepEqual( output.toString(), EXPECTED.toString(), 'returns bundle' );

		// Rename file to `index.js`:
		rename( newPath, oldPath );
		t.end();
	}
});

tape( 'the function bundles benchmarks into a single file using `browserify` (custom glob pattern)', function test( t ) {
	var opts = {
		'pattern': '*.js'
	};
	bundle( FIXTURES, opts, onRead );

	function onRead( error, output ) {
		if ( error ) {
			t.ok( false, 'did not expect an error' );
		}
		t.deepEqual( output.toString(), EXPECTED.toString(), 'returns bundle' );
		t.end();
	}
});

tape( 'the function bundles benchmarks into a single file using `browserify` and writes the results to disk', function test( t ) {
	var opath = join( FIXTURES, 'benchmarks.html' );
	var opts = {
		'out': opath,
		'pattern': '*.js'
	};
	bundle( FIXTURES, opts, onWrite );

	function onWrite( error ) {
		var out;
		if ( error ) {
			t.ok( false, 'did not expect an error' );
		}
		out = readFile( opath );
		t.deepEqual( out.toString(), EXPECTED.toString(), 'written file contains expected contents' );

		// Remove created file:
		unlink( opath );
		t.end();
	}
});

tape( 'the function invokes the callback with an error if writing to disk fails', function test( t ) {
	var opath = join( FIXTURES, 'nonexisting', 'bundle.js' );
	var opts = {
		'out': opath,
		'pattern': '*.js'
	};
	bundle( FIXTURES, opts, onWrite );

	function onWrite( error ) {
		if ( error ) {
			t.ok( true, error.message );
		} else {
			t.ok( false, 'expected an error' );
		}
		t.end();
	}
});
