var startcase = require( '@stdlib/string/startcase' );
var uncapitalize = require( '@stdlib/string/uncapitalize' );


// VARIABLES //

var RE_SEPS = /[_\-]/g; // eslint-disable-line no-useless-escape


// MAIN //

/**
* Converts a package basename to camelcase but retain leading underscores.
*
* @private
* @param {string} name - package basename
* @returns {string} alias
*
* @example
* var out = camelcase( 'foo-bar' );
* // returns 'fooBar'
*
* @example
* var out = camelcase( '_foo-bar' );
* // returns '_fooBar'
*
* @example
* var out = camelcase( 'foo' );
* // returns 'foo'
*/
