'use strict';

/**
* Create a custom bundle from a list of packages.
*
* @module @stdlib/_tools/bundle/pkg-list
*
* @example
* var bundle = require( '@stdlib/_tools/bundle/pkg-list' );
*
* var pkgs = [
*     '@stdlib/math/base/special/erf',
*     '@stdlib/math/base/special/gamma'
* ];
*
* bundle( pkgs, onBundle );
*
* function onBundle( error, bundle ) {
*     if ( error ) {
*         throw error;
*     }
*     console.log( bundle.toString() );
* }
*/

// MODULES //

var bundle = require( './main.js' );


// EXPORTS //

module.exports = bundle;
