'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'function square( x ) {',
		'  var x;',
		'  // Square the number:',
		'  return x*x;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Missing empty line before inline comment',
			'type': 'FunctionDeclaration'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'function fizzBuzz( x ) {',
		'  var out;',
		'  var i;',
		'',
		'  for ( i = 1; i <= 100; i++ ) {',
		'    out = ( i % 5 === 0 ) ? "Buzz" : ( i % 3 === 0 ) ? "Fizz" : i;',
		'    // Print to console: ',
		'    console.log( out );',
		'  }',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Missing empty line before inline comment',
			'type': 'FunctionDeclaration'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
