'use strict';

// VARIABLES //

var invalid;
var test;


// MAIN //

// Create our test cases:
invalid = [];

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'*     x = y;',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Code blocks should be fenced',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'* ```javascript',
		'* x = y;',
		'* ```',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'options': [ 'indented' ],
	'errors': [
		{
			'message': 'Code blocks should be indented',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'*     x = y;',
		'*',
		'* ## Heading',
		'*',
		'* ```javascript',
		'* z = y;',
		'* ```',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'options': [ 'consistent' ],
	'errors': [
		{
			'message': 'Code blocks should be indented',
			'type': null
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
