'use strict';

// VARIABLES //

var invalid;
var test;


// MAIN //

// Create our test cases:
invalid = [];

test = {
	'code': [
		'/**',
		'* Beep *boop*.',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Emphasis should use `_` as a marker',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Beep *boop*.',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'options': [ '_' ],
	'errors': [
		{
			'message': 'Emphasis should use `_` as a marker',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Beep _boop_.',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'options': [ '*' ],
	'errors': [
		{
			'message': 'Emphasis should use `*` as a marker',
			'type': null
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
