'use strict';

// VARIABLES //

var invalid;
var test;


// MAIN //

// Create our test cases:
invalid = [];

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'* ~~~javascript',
		'* y = x;',
		'* ~~~',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Fenced code should use ` as a marker',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'* ~~~javascript',
		'* y = x;',
		'* ~~~',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'options': [ '`' ],
	'errors': [
		{
			'message': 'Fenced code should use ` as a marker',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Beep _boop_.',
		'*',
		'* ```javascript',
		'* y = x;',
		'* ```',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'options': [ '~' ],
	'errors': [
		{
			'message': 'Fenced code should use ~ as a marker',
			'type': null
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
