'use strict';

// VARIABLES //

var invalid;
var test;


// MAIN //

// Create our test cases:
invalid = [];

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'* ## Beep!',
		'*',
		'* boop',
		'*',
		'* ## Boop?',
		'*',
		'* beep',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Don’t add a trailing `!` to headings',
			'type': null
		},
		{
			'message': 'Don’t add a trailing `?` to headings',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'* ## Beep.',
		'*',
		'* boop',
		'*',
		'* ## Boop,',
		'*',
		'* beep',
		'*',
		'* ## Foo:',
		'*',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Don’t add a trailing `.` to headings',
			'type': null
		},
		{
			'message': 'Don’t add a trailing `,` to headings',
			'type': null
		},
		{
			'message': 'Don’t add a trailing `:` to headings',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'* ## Beep',
		'*',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'options': [ 'p' ],
	'errors': [
		{
			'message': 'Don’t add a trailing `p` to headings',
			'type': null
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
