'use strict';

// VARIABLES //

var invalid;
var test;


// MAIN //

// Create our test cases:
invalid = [];

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'* -	This a list item.',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Use spaces instead of hard-tabs',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/**',
		'* Beep boop.',
		'*',
		'* >\tThis a blockquote.',
		'*',
		'*/',
		'function beep() {',
		'    console.log( "boop" );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Use spaces instead of hard-tabs',
			'type': null
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
