'use strict';

var valid = [];
var test;

test = {
	'code': [
		'/**',
		'* Returns a pseudo-random number on [0,1].',
		'* ',
		'* @returns {number} uniform random number',
		'*',
		'* @example',
		'* var y = rand();',
		'* // e.g., returns 0.5363925252089496',
		'*/',
		'function rand( x ) {',
		'  return Math.random();',
		'}'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'/**',
		'* Tests if a value is `undefined` or `null`.',
		'*',
		'* @param {*} value - value to test',
		'* @returns {boolean} boolean indicating whether value is undefined',
		'*',
		'* @example',
		'* var bool = isUndefinedOrNull( undefined );',
		'* // returns true',
		'*',
		'* bool = isUndefinedOrNull( null );',
		'* // returns true',
		'*',
		'* bool = isUndefinedOrNull( false );',
		'* // returns false',
		'*/',
		'function isUndefinedOrNull( value ) {',
		'  return ( value === void 0 || value === null );',
		'}'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var isLowercase = require( \'./../lib\' );',
		'',
		'console.log( isLowercase( \'hello\' ) );',
		'// => true',
		'',
		'console.log( isLowercase( \'\' ) );',
		'// => false',
		'',
		'console.log( isLowercase( \'Hello\' ) );',
		'// => false'
	].join( '\n' )
};
valid.push( test );


// EXPORTS //

module.exports = valid;
