'use strict';

var Linter = require( 'eslint' ).Linter;
var rule = require( './../lib' );

var linter = new Linter();
var result;

var code = 'var arr = Array( 10 );';

linter.defineRule( 'new-cap-array', rule );

result = linter.verify( code, {
	'rules': {
		'new-cap-array': 'error'
	}
});
console.log( result );
