'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'var arr = Array( 10 );'
	].join( '\n' ),
	'errors': [
		{
			'message': '`Array` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// MAIN //',
		'',
		'function zeros( n ) {',
		'  var arr = Array( n );',
		'  for ( var i = 0; i < n; i++ ) {',
		'    arr[ i ] = 0;',
		'  }',
		'  return arr;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': '`Array` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
