'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'var err = Error();'
	].join( '\n' ),
	'errors': [
		{
			'message': '`Error` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// MAIN //',
		'',
		'function sqrt( x ) {',
		'  if ( x < 0 ) {',
		'    throw TypeError( \'must provide a non-negative number\' );',
		'  }',
		'  return Math.sqrt( x );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': '`TypeError` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
