'use strict';

var Linter = require( 'eslint' ).Linter;
var rule = require( './../lib' );

var linter = new Linter();
var result;

var code = 'var re = RegExp( \'ab+c\', \'i\' );';

linter.defineRule( 'new-cap-regexp', rule );

result = linter.verify( code, {
	'rules': {
		'new-cap-regexp': 'error'
	}
});
console.log( result );
