'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'var re = RegExp( \'ab+c\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': '`RegExp` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var re = RegExp( \'ab+c\', \'i\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': '`RegExp` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// MAIN //',
		'',
		'function createGlobalRegexp( str ) {',
		'  var re = RegExp( str, \'g\' );',
		'  return re;',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': '`RegExp` constructor is invoked without the `new` keyword',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
