'use strict';

var valid = [];
var test;

test = {
	'code': 'var re = new RegExp( \'ab+c\' );'
};
valid.push( test );

test = {
	'code': 'var re = new RegExp( /ab+c/ );'
};
valid.push( test );

test = {
	'code': 'var re = new RegExp( \'ab+c\', \'i\' );'
};
valid.push( test );

test = {
	'code': [
		'// MAIN //',
		'',
		'function createGlobalRegexp( str ) {',
		'  var re = new RegExp( str, \'g\' );',
		'  return re;',
		'}'
	].join( '\n' )
};
valid.push( test );


// EXPORTS //

module.exports = valid;
