'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'// MODULES //',
		'',
		'var name = \'@stdlib/math/base/special/betainc\';',
		'var betainc = require( name );'
	].join( '\n' ),
	'errors': [
		{
			'message': 'require() calls should only use string literals',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var math = require( \'@stdlib/\' + \'math\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': 'require() calls should only use string literals',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
