'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'// MODULES //',
		'',
		'var debug = require( \'debug\' )( \'stdlib\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': 'require() expressions should not be immediately invoked',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var pdf = require( \'@stdlib/math/base/dists/normal/pdf\' ).factory( 0.0, 1.0 );'
	].join( '\n' ),
	'errors': [
		{
			'message': 'require() expressions should not be immediately invoked',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
