'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'// MODULES //',
		'var betainc = require( \'@stdlib/math/base/special/betainc/lib/betainc.js\' )'
	].join( '\n' ),
	'errors': [
		{
			'message': 'require() call contains a path which loads a module internal to a stdlib package, and, thus, should be considered private',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// MODULES //',
		'var betaincinv = require( \'@stdlib/math/base/special/betaincinv\' )',
		'var kernelBetainc = require( \'@stdlib/math/base/special/kernel-betainc/lib/ibeta_roots.js\' )'
	].join( '\n' ),
	'errors': [
		{
			'message': 'require() call contains a path which loads a module internal to a stdlib package, and, thus, should be considered private',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
