'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'var y;',
		'var y;'
	].join( '\n' ),
	'errors': [
		{
			'message': 'y is already defined',
			'type': 'Identifier'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// VARIABLES //',
		'var x = 2;',
		'var x = 4;'
	].join( '\n' ),
	'errors': [
		{
			'message': 'x is already defined',
			'type': 'Identifier'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var Object = 0;'
	].join( '\n' ),
	'options': [{
		'builtinGlobals': true
	}],
	'errors': [
		{
			'message': 'Object is already defined',
			'type': 'Identifier'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/* global Float64Array */',
		'',
		'// MODULES //',
		'',
		'var Float64Array = require( \'@stdlib/array/float64\' );'
	].join( '\n' ),
	'options': [{
		'builtinGlobals': true
	}],
	'errors': [
		{
			'message': 'Float64Array is already defined',
			'type': 'Identifier'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'/* global Float64Array */',
		'',
		'// MODULES //',
		'',
		'var Float64Array = require( \'@stdlib/array/float64\' );',
		'var Float64Array = require( \'@stdlib/array/float64\' );'
	].join( '\n' ),
	'options': [{
		'builtinGlobals': false
	}],
	'errors': [
		{
			'message': 'Float64Array is already defined',
			'type': 'Identifier'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
