'use strict';

var valid = [];
var test;

test = {
	'code': [
		'// MODULES //',
		'',
		'var betainc = require( \'@stdlib/math/base/special/betainc\' )'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// VARIABLES //',
		'',
		'var x = 3;',
		'var y = 2;'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'// VARIABLES //',
		'',
		'var Object = 0;'
	].join( '\n' ),
	'options': [{
		'builtinGlobals': false
	}]
};
valid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var Object = 0;'
	].join( '\n' ),
	'options': [{
		'builtinGlobals': true,
		'globalsWhitelist': [ 'Object' ]
	}]
};
valid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var Object = 0;'
	].join( '\n' ),
	'options': [{
		'builtinGlobals': true
	}],
	'parserOptions': {
		'sourceType': 'module'
	}
};
valid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var Object = 0;'
	].join( '\n' ),
	'options': [{
		'builtinGlobals': true
	}],
	'parserOptions': {
		'ecmaFeatures': {
			'globalReturn': true
		}
	}
};
valid.push( test );

test = {
	'code': [
		'/* global Float64Array */',
		'',
		'// MODULES //',
		'',
		'var Float64Array = require( \'@stdlib/types/array/float64\' );'
	].join( '\n' ),
	'options': [{
		'builtinGlobals': false
	}],
	'errors': [
		{
			'message': 'Float64Array is already defined',
			'type': 'Identifier'
		}
	]
};
valid.push( test );

test = {
	'code': [
		'/* global Float64Array */',
		'',
		'// MODULES //',
		'',
		'var Float64Array = require( \'@stdlib/types/array/float64\' );'
	].join( '\n' ),
	'options': [{
		'builtinGlobals': true,
		'globalsWhitelist': [ 'Float64Array' ]
	}],
	'errors': [
		{
			'message': 'Float64Array is already defined',
			'type': 'Identifier'
		}
	]
};
valid.push( test );


// EXPORTS //

module.exports = valid;
