'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'// MODULES //',
		'',
		'var stdlib = require( \'/path/to/stdlib\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': 'require() call contains an absolute file path',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'require( \'/path/to/stdlib\' )'
	].join( '\n' ),
	'errors': [
		{
			'message': 'require() call contains an absolute file path',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// MAIN //',
		'',
		'setReadOnly( ns, \'math\', require( \'/path/to/stdlib/math\' ) );'
	].join( '\n' ),
	'errors': [
		{
			'message': 'require() call contains an absolute file path',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'// MAIN //',
		'',
		'var ns = {',
		'  \'math\': require( \'/path/to/stdlib/math\' )',
		'};'
	].join( '\n' ),
	'errors': [
		{
			'message': 'require() call contains an absolute file path',
			'type': 'CallExpression'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
