'use strict';

var Linter = require( 'eslint' ).Linter;
var rule = require( './../lib' );

var linter = new Linter();
var result;

var code = 'require( \'@stdlib/math\' );';

linter.defineRule( 'no-unassigned-require', rule );

result = linter.verify( code, {
	'rules': {
		'no-unassigned-require': 'error'
	}
});
console.log( result );
