'use strict';

// VARIABLES //

var rule;


// FUNCTIONS //

/**
* Rule for validating that required packages are assigned to variables.
*
* @param {Object} context - ESLint context
* @returns {Object} validators
*/
function main( context ) {
	/**
	* Reports the error message.
	*
	* @private
	* @param {ASTNode} node - node to report
	*/
	function report( node ) {
		context.report({
			'node': node,
			'message': 'result of `require()` call is not assigned to a variable'
		});
	}

	/**
	* Checks whether required packages are assigned to variables.
	*
	* @private
	* @param {ASTNode} node - node to examine
	*/
	function validate( node ) {
		if (
			node.expression.type === 'CallExpression' &&
			node.expression.callee &&
			node.expression.callee.type === 'Identifier' &&
			node.expression.callee.name === 'require'
		) {
			report( node );
		}
	}

	return {
		'ExpressionStatement': validate
	};
}


// MAIN //

rule = {
	'meta': {
		'docs': {
			'description': 'enforce that required packages are assigned to variables'
		},
		'schema': []
	},
	'create': main
};


// EXPORTS //

module.exports = rule;
