'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'// MODULES //',
		'',
		'require( \'@stdlib/math/\' );'
	].join( '\n' ),
	'errors': [
		{
			'message': 'result of `require()` call is not assigned to a variable',
			'type': 'ExpressionStatement'
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'function init() {',
		'  require( \'@stdlib/math/\' );',
		'}'
	].join( '\n' ),
	'errors': [
		{
			'message': 'result of `require()` call is not assigned to a variable',
			'type': 'ExpressionStatement'
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
