'use strict';

var Linter = require( 'eslint' ).Linter;
var rule = require( './../lib' );

var linter = new Linter();
var result;

var code = 'var arr = new Int32Array( [1,2,3] );';

linter.defineRule( 'require-globals', rule );

result = linter.verify( code, {
	'rules': {
		'require-globals': [ 'error', {
			'globals': [
				'Int32Array'
			]
		}]
	}
});
console.log( result );
