'use strict';

// VARIABLES //

var opts = {
	'globals': [
		'ArrayBuffer',
		'Buffer',
		'DataView',
		'Float32Array',
		'Float64Array',
		'Int8Array',
		'Int16Array',
		'Int32Array',
		'SharedArrayBuffer',
		'Uint8Array',
		'Uint8ClampedArray',
		'Uint16Array',
		'Uint32Array'
	]
};


// MAIN //

var valid = [];
var test;

test = {
	'code': [
		'// MODULES //',
		'',
		'var Int32Array = require( \'@stdlib/array/int32\' )',
		'',
		'// VARIABLES //',
		'var arr = new Int32Array( [1,2,3] );'
	].join( '\n' ),
	'options': [ opts ]
};
valid.push( test );

test = {
	'code': [
		'// MODULES //',
		'',
		'var Int32Array = require( \'@stdlib/array/int32\' )',
		'',
		'// MAIN //',
		'',
		'function dice() {',
		'  return new Int32Array( [1,2,3,4,5,6] );',
		'}'
	].join( '\n' ),
	'options': [ opts ]
};
valid.push( test );


// EXPORTS //

module.exports = valid;
