'use strict';

var Linter = require( 'eslint' ).Linter;
var rule = require( './../lib' );

var linter = new Linter();
var result;

var code = [
	'var validate = require( \'./validate.js\' )',
	'var fs = require( \'fs\' )'
].join( '\n' );

linter.defineRule( 'require-order', rule );

result = linter.verify( code, {
	'rules': {
		'require-order': 'error'
	}
});
console.log( result );
