'use strict';

var invalid = [];
var test;

test = {
	'code': [
		'var isLowercase = require( \'@stdlib/assert/is-lowercase\' );',
		'',
		'console.log( isLowercase( \'hello\' ) );',
		'// => false',
		'',
		'console.log( isLowercase( \'\' ) );',
		'// => false',
		'',
		'console.log( isLowercase( \'Hello\' ) );',
		'// => true'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Displayed return value is `false`, but function returns `true` instead',
			'type': null
		},
		{
			'message': 'Displayed return value is `true`, but function returns `false` instead',
			'type': null
		}
	]
};
invalid.push( test );

test = {
	'code': [
		'var isLowercase = require( \'@stdlib/assert/is-lowercase\' );',
		'',
		'var bool = isLowercase( \'hello\' );',
		'// returns false',
		'',
		'bool = isLowercase( \'\' );',
		'// returns false',
		'',
		'bool = isLowercase( \'Hello\' );',
		'// returns true'
	].join( '\n' ),
	'errors': [
		{
			'message': 'Displayed return value is `false`, but function returns `true` instead',
			'type': null
		},
		{
			'message': 'Displayed return value is `true`, but function returns `false` instead',
			'type': null
		}
	]
};
invalid.push( test );


// EXPORTS //

module.exports = invalid;
