'use strict';

var valid = [];
var test;

test = {
	'code': [
		'function fizzBuzz( x ) {',
		'  // This comment is okay...',
		'  var out;',
		'  var i;',
		'',
		'  for ( i = 1; i <= 100; i++ ) {',
		'    // This comment is okay...',
		'    out = ( i % 5 === 0 ) ? "Buzz" : ( i % 3 === 0 ) ? "Fizz" : i;',
		'    console.log( out );',
		'  }',
		'}'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'/* eslint-disable max-len */',
		'',
		'var a = 2 * 4;'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'/**',
		'* Sums two elements.',
		'*',
		'* @param {number} x - first number',
		'* @param {number} y - second number',
		'* @returns {number} x + y',
		'*/',
		'function add( x, y ) {',
		'  return x + y;',
		'}'
	].join( '\n' )
};
valid.push( test );

test = {
	'code': [
		'var MAX = 10; // BIG'
	].join( '\n' )
};
valid.push( test );


