'use strict';

/**
* Retrieve resources from a GitHub API endpoint.
*
* @module @stdlib/_tools/github/get
*
* @example
* var get = require( '@stdlib/_tools/github/get' );
*
* get( onResponse );
*
* function onResponse( error, data, info ) {
*     // Check for rate limit info...
*     if ( info ) {
*         console.error( 'Limit: %d', info.limit );
*         console.error( 'Remaining: %d', info.remaining );
*         console.error( 'Reset: %s', (new Date( info.reset*1000 )).toISOString() );
*     }
*     if ( error ) {
*         if ( error instanceof Error ) {
*             throw error;
*         }
*         console.error( error.message );
*     } else {
*         console.log( JSON.stringify( data ) );
*     }
* }
*
* @example
* var factory = require( '@stdlib/_tools/github/get' ).factory;
*
* function onResponse( error, data, info ) {
*     // Check for rate limit info...
*     if ( info ) {
*         console.error( 'Limit: %d', info.limit );
*         console.error( 'Remaining: %d', info.remaining );
*         console.error( 'Reset: %s', (new Date( info.reset*1000 )).toISOString() );
*     }
*     if ( error ) {
*         if ( error instanceof Error ) {
*             throw error;
*         }
*         console.error( error.message );
*     } else {
*         console.log( JSON.stringify( data ) );
*     }
* }
*
* var options = {
*     'pathname': '/user/repos',
*     'page': 1,
*     'per_page': 100
* };
*
* var getRepos = factory( options, onReponse );
*
* // Make repeated requests:
* getRepos();
* getRepos();
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var get = require( './main.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( get, 'factory', factory );


// EXPORTS //

module.exports = get;
