'use strict';

// MAIN //

/**
* Determines the total number of pages to resolve.
*
* @private
* @param {PositiveInteger} first - first page to resolve
* @param {PositiveInteger} last - last page to resolve
* @returns {PositiveInteger} total number of pages to resolve
*/
function getTotal( first, last ) {
	return last - (first-1);
}


// EXPORTS //

module.exports = getTotal;
