'use strict';

// MODULES //

var EventEmitter = require( 'events' ).EventEmitter;
var noop = require( '@stdlib/utils/noop' );
var inherit = require( '@stdlib/utils/inherit' );
var data = require( './results.json' );


// MAIN //

/**
* Mock HTTP response constructor.
*
* @private
* @constructor
* @param {NonNegativeInteger} statusCode - mock status code
* @returns {Response} mock HTTP response object
*/
function Response( statusCode ) { // eslint-disable-line stdlib/no-redeclare
	var self;
	if ( !( this instanceof Response ) ) {
		return new Response( statusCode );
	}
	EventEmitter.call( this );
	this.statusCode = statusCode;

	self = this;
	setTimeout( onTimeout, 0 );

	return this;

	/**
	* Callback invoked in the next event loop.
	*
	* @private
	* @returns {void}
	*/
	function onTimeout() {
		if ( statusCode !== 200 ) {
			self.emit( 'data', 'bad request' );
			return self.emit( 'end' );
		}
		self.emit( 'data', JSON.stringify( data ) );
		self.emit( 'end' );
	}
} // end FUNCTION Response()

inherit( Response, EventEmitter );
Response.prototype.setEncoding = noop;


// EXPORTS //

module.exports = Response;
