'use strict';

// MODULES //

var tape = require( 'tape' );
var get = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.equal( typeof get, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a factory method', function test( t ) {
	t.equal( typeof get.factory, 'function', 'has method' );
	t.end();
});
