'use strict';

// MAIN //

var types = [
	// Variables and functions:
	'VariableDeclaration',
	'FunctionDeclaration',
	'FunctionExpression',

	// Statements:
	'ExpressionStatement',
	'ReturnStatement',

	// Control flow:
	'IfStatement',
	'SwitchStatement',
	'ConditionalExpression', // i.e., a ternary expression

	// Loops:
	'WhileStatement',
	'DoWhileStatement',
	'ForStatement',
	'ForInStatement',

	// Loop/control:
	'ContinueStatement',
	'BreakStatement',

	// Exception handling:
	'ThrowStatement',
	'TryStatement'
];


// EXPORTS //

module.exports = types;
