'use strict';

// MODULES //

var TABLE = require( './lloc_table.js' );


// MAIN //

/**
* Calculates the number of logical lines of code.
*
* @private
* @param {Array<Node>} nodes - list of nodes
* @returns {NonNegativeInteger} number of logical lines of code
*/
function lloc( nodes ) {
	var count;
	var node;
	var type;
	var inc;
	var i;

	count = 0;
	for ( i = 0; i < nodes.length; i++ ) {
		node = nodes[ i ];
		type = node.type;
		inc = TABLE[ type ];
		if ( inc ) {
			count += inc;
		}
	}
	return count;
} // end FUNCTION lloc()


// EXPORTS //

module.exports = lloc;
