'use strict';

/**
* Lint header filenames.
*
* @module @stdlib/_tools/lint/header-filenames
*
* @example
* var lint = require( '@stdlib/_tools/lint/header-filenames' );
*
* lint( onLint );
*
* function onLint( error, errs ) {
*     if ( error ) {
*         throw error;
*     }
*     console.error( errs );
* }
*
* @example
* var lint = require( '@stdlib/_tools/lint/header-filenames' );
*
* var errs = lint.sync();
* // returns [...]
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var lint = require( './async.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( lint, 'sync', sync );


// EXPORTS //

module.exports = lint;
