'use strict';

// MODULES //

var basename = require( 'path' ).basename; // TODO: replace with stdlib pkg
var logger = require( 'debug' );
var readFile = require( '@stdlib/fs/read-file' );
var extname = require( '@stdlib/utils/extname' );
var RE_EOL = require( '@stdlib/regexp/eol' );


// VARIABLES //

var debug = logger( 'lint:license-header-glob:linter:async' );
var FOPTS = {
	'encoding': 'utf8'
};


// MAIN //

/**
* Asynchronously lints each file in a provided file list.
*
* @private
* @param {StringArray} files - file list
* @param {Object} header - object containing license headers against which to lint
* @param {Callback} clbk - callback to invoke upon completion
* @returns {void}
*/
function lint( files, header, clbk ) {
	var total;
	var errs;
	var idx;

	total = files.length;
	idx = -1;
	errs = [];
	return next();

	/**
	* Processes the next file.
	*
	* @private
	*/
	function next() {
		idx += 1;
		debug( 'Linting file (%d of %d): %s', idx+1, total, files[ idx ] );
		readFile( files[ idx ], FOPTS, onRead );
	} // end FUNCTION next()

	/**
	* Callback invoked upon reading a file.
	*
	* @private
	* @param {(Error|null)} error - error object
	* @param {string} file - file contents
	* @returns {void}
	*/
	function onRead( error, file ) {
		var ext;
		var tmp;
		var re;
		if ( error ) {
			debug( 'Encountered an error when attempting to read file: %s. Error: %s', files[ idx ], error.message );
			return done( error );
		}
		ext = extname( files[ idx ] ).slice( 1 ); // ignore leading `.`
		re = header[ ext ];
		if ( !re ) {
			// E.g., account for files such as `Makefile` which lack a filename extension...
			re = header[ basename( files[ idx ] ) ] || header.default || null;
		}
		if ( re ) {
			tmp = file.match( re );
			if ( tmp === null ) {
				errs.push({
					'name': files[ idx ],
					'error': 'file either has a malformed license header or does not contain a license header.'
				});
			} else if ( tmp.length > 1 ) {
				errs.push({
					'name': files[ idx ],
					'error': 'file contains more than 1 license header.'
				});
			} else {
				// Check header location...
				tmp = file.search( re );
				tmp = file.substring( 0, tmp );
				tmp = tmp.split( RE_EOL ).length;
				if ( tmp > 4 ) {
					errs.push({
						'name': files[ idx ],
						'error': 'license header should begin within the first 5 lines of the file. Starting line: '+tmp+'.'
					});
				}
			}
		}
		done();
	} // end FUNCTION onRead()

	/**
	* Callback invoked upon linting a file.
	*
	* @private
	* @param {(Error|null)} error - error object
	* @returns {void}
	*/
	function done( error ) {
		if ( error ) {
			return clbk( error );
		}
		debug( 'Finished linting file %d of %d.', idx+1, total );
		if ( idx === total-1 ) {
			return clbk( null, errs );
		}
		next();
	} // end FUNCTION done()
} // end FUNCTION lint()


// EXPORTS //

module.exports = lint;
