'use strict';

// MODULES //

var namespace = require( '@stdlib/namespace' );


// MAIN //

/**
* Returns an object mapping package identifiers to aliases.
*
* @private
* @returns {Object} object mapping package identifiers to aliases
*/
function aliases() {
	var alias;
	var out;
	var ns;
	var i;

	ns = namespace();
	out = {};

	for ( i = 0; i < ns.length; i++ ) {
		// Some aliases are `.` separated to indicate a sub-namespace (e.g., `base.erf`, `base.dist.arcsine.pdf`, etc). We only care about the final alias...
		alias = ns[ i ].alias.split( '.' );
		out[ ns[ i ].path ] = alias[ alias.length-1 ];
	}
	return out;
} // end FUNCTION aliases()


// EXPORTS //

module.exports = aliases();
