'use strict';

/**
* Lint namespace aliases.
*
* @module @stdlib/_tools/lint/namespace-aliases
*
* @example
* var lint = require( '@stdlib/_tools/lint/namespace-aliases' );
*
* lint( onLint );
*
* function onLint( error, errs ) {
*     if ( error ) {
*         throw error;
*     }
*     if ( errs.length ) {
*         console.error( JSON.stringify( errs ) );
*     } else {
*         console.error( 'No detected errors.' );
*     }
* }
*
* @example
* var lintSync = require( '@stdlib/_tools/lint/namespace-aliases' ).sync;
*
* var errs = lintSync();
* if ( errs.length ) {
*     console.error( JSON.stringify( errs ) );
* } else {
*     console.error( 'No detected errors' );
* }
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var lint = require( './async.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( lint, 'sync', sync );


// EXPORTS //

module.exports = lint;
