'use strict';

/**
* Tests if an AST node is a namespace assignment.
*
* @private
* @param {Node} node - AST node
* @returns {boolean} boolean indicating if node is a namespace assignment
*/
function isAssignment( node ) {
	return (
		node.callee.type === 'Identifier' &&
		node.callee.name === 'setReadOnly'
	);
} // end FUNCTION isAssignment()


// EXPORTS //

module.exports = isAssignment;
