'use strict';

// MODULES //

var nthIndex = require( './nth.js' );


// MAIN //

/**
* Replace all forward slashes after the initial one with dashes to form a valid standalone package name.
*
* @private
* @param {string} pkg - package tree path
* @returns {string} valid package name
*/
function standalonePackage( pkg ) {
	var idx = nthIndex( pkg, '/', 1 );
	return pkg.replace( /\//g, replaceSlashes );

	/**
	* Replace a forward slash if index is not equal to first match.
	*
	* @private
	* @param {string} s - matched slash
	* @param {number} i - match index
	* @returns {string} replacement character (`-` or `/`)
	*/
	function replaceSlashes( s, i ) {
		return ( i > idx ) ? '-' : s;
	}
}


// EXPORTS //

module.exports = standalonePackage;
