'use strict';

/**
* Find package READMEs.
*
* @module @stdlib/_tools/pkgs/readmes
*
* @example
* var findREADMEs = require( '@stdlib/_tools/pkgs/readmes' );
*
* findREADMEs( clbk );
*
* function clbk( error, files ) {
*     if ( error ) {
*         throw error;
*     }
*     console.dir( files );
* }
*
* @example
* var findREADMEs = require( '@stdlib/_tools/pkgs/readmes' );
*
* var files = findREADMEs.sync();
* // returns [...]
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var findREADMEs = require( './main.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( findREADMEs, 'sync', sync );


// EXPORTS //

module.exports = findREADMEs;
