'use strict';

// MODULES //

var glob = require( 'glob' ).sync;
var join = require( 'path' ).join;
var resolve = require( 'path' ).resolve;
var cwd = require( '@stdlib/utils/cwd' );
var copy = require( '@stdlib/utils/copy' );
var dirname = require( '@stdlib/utils/dirname' );
var exists = require( '@stdlib/fs/exists' ).sync;
var config = require( './config.json' );
var validate = require( './validate.js' );


// MAIN //

/**
* Synchronously finds package READMEs.
*
* @param {Options} [options] - function options
* @param {string} [options.dir] - root directory from which to search for package READMEs
* @param {string} [options.pattern='**\/package.json'] - glob pattern
* @param {StringArray} [options.ignore] - glob pattern(s) to exclude matches
* @throws {TypeError} options argument must be an object
* @throws {TypeError} must provide valid options
* @throws {Error} `pattern` option must end with `package.json`
* @returns {(EmptyArray|StringArray)} list of READMEs
*
* @example
* var files = findREADMEs();
* // returns [...]
*/
function findREADMEs( options ) {
	var gopts;
	var files;
	var fpath;
	var opts;
	var err;
	var dir;
	var out;
	var i;

	opts = copy( config );
	if ( arguments.length ) {
		err = validate( opts, options );
		if ( err ) {
			throw err;
		}
	}
	if ( opts.dir ) {
		dir = resolve( cwd(), opts.dir );
	} else {
		dir = cwd();
	}
	// Find `package.json` files...
	gopts = {
		'cwd': dir,
		'ignore': opts.ignore,
		'realpath': true // return absolute file paths
	};
	files = glob( opts.pattern, gopts );

	// Search for package READMEs...
	out = [];
	for ( i = 0; i < files.length; i++ ) {
		dir = dirname( files[ i ] );
		fpath = join( dir, 'README.md' );
		if ( exists( fpath ) ) {
			out.push( fpath );
		}
	}
	return out;
} // end FUNCTION findREADMEs()


// EXPORTS //

module.exports = findREADMEs;
