'use strict';

// MODULES //

var tape = require( 'tape' );
var proxyquire = require( 'proxyquire' );
var isStringArray = require( '@stdlib/assert/is-string-array' ).primitives;
var isArray = require( '@stdlib/assert/is-array' );
var find = require( './../lib/find.js' ); // eslint-disable-line stdlib/no-redeclare


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof find, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a string array if able to find at least one package README', function test( t ) {
	var count;
	var find;

	find = proxyquire( './../lib/find.js', {
		'@stdlib/fs/exists': exists
	});
	count = 0;

	find( [ '/beep/boop/package.json', '/a/b/c/d/e/f/g/h/beep/boop/bop/package.json' ], clbk );

	function exists() {
		var clbk = arguments[ arguments.length-1 ];
		count += 1;
		if ( count < 2 ) {
			return clbk( null, true );
		}
		clbk( new Error( 'not found' ), false );
	}

	function clbk( error, files ) {
		if ( error ) {
			t.ok( false, error.message );
		}
		t.strictEqual( isStringArray( files ), true, 'returns a string array' );
		t.strictEqual( files.length, 1, 'expected length' );
		t.end();
	}
});

tape( 'the function returns an empty array if unable to find at least one package README', function test( t ) {
	var find = proxyquire( './../lib/find.js', {
		'@stdlib/fs/exists': exists
	});

	find( [ '/a/b/c/d/e/f/g/h/beep/boop/bop/package.json' ], clbk );

	function exists() {
		var clbk = arguments[ arguments.length-1 ];
		clbk( new Error( 'not found' ), false );
	}

	function clbk( error, files ) {
		if ( error ) {
			t.ok( false, error.message );
		}
		t.strictEqual( isArray( files ), true, 'returns an array' );
		t.strictEqual( files.length, 0, 'returns an empty array' );
		t.end();
	}
});
