'use strict';

// MODULES //

var tape = require( 'tape' );
var findREADMEs = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof findREADMEs, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to synchronously find package READMEs', function test( t ) {
	t.strictEqual( typeof findREADMEs.sync, 'function', 'has sync method' );
	t.end();
});
