'use strict';

// MAIN //

/**
* Returns the first index of a specified character in a string.
*
* @private
* @param {string} str - input string
* @param {string} searchValue - search value
* @returns {integer} index
*
* @example
* var idx = indexOf( '@stdlib/foo', '/' );
* // returns 7
*
* @example
* var idx = indexOf( '@stdlib/foo/bar', '/' );
* // returns 7
*/
function indexOf( str, searchValue ) {
	var i;
	for ( i = 0; i < str.length; i++ ) {
		if ( str[ i ] === searchValue ) {
			return i;
		}
	}
	return -1;
}


// EXPORTS //

module.exports = indexOf;
