'use strict';

// MODULES //

var replace = require( '@stdlib/string/replace' );
var indexOf = require( './index_of.js' );


// VARIABLES //

var RE = /\//g;


// MAIN //

/**
* Transforms an "internal" package (e.g., `@stdlib/math/base/special/sin`) to a standalone package name (e.g., `@stdlib/math-base-special-sin`).
*
* @private
* @param {(StringArray|EmptyArray)} pkgs - list of package names
* @returns {(StringArray|EmptyArray)} transformed package names
*
* @example
* var pkgs = [
*     '@stdlib/math/base/special/sin',
*     '@stdlib/datasets/sotu',
*     '@stdlib/math/base/special',
*     '@stdlib/datasets'
* ];
*
* var out = transform( pkgs );
* // returns [ '@stdlib/math-base-special-sin', '@stdlib/datasets-sotu', '@stdlib/math-base-special', '@stdlib/datasets' ]
*/
function transform( pkgs ) {
	var idx;
	var pkg;
	var i;

	for ( i = 0; i < pkgs.length; i++ ) {
		pkg = pkgs[ i ];

		// Find the first forward slash:
		idx = indexOf( pkg, '/' );

		// Replace all forward slashes after the first forward slash:
		idx += 1;
		pkgs[ i ] = pkg.slice( 0, idx ) + replace( pkg.slice( idx ), RE, '-' );
	}
	return pkgs;
}


// EXPORTS //

module.exports = transform;
