'use strict';

// MODULES //

var tape = require( 'tape' );
var findWASM = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof findWASM, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to synchronously find packages containing WebAssembly', function test( t ) {
	t.strictEqual( typeof findWASM.sync, 'function', 'has sync method' );
	t.end();
});
