'use strict';

/**
* Wraps a source code string in an immediately invoked function (IIFE) in a manner similar to `require`.
*
* @private
* @param {string} src - source code
* @param {string} filename - filename
* @param {string} dirname - directory name
* @returns {string} wrapped code
*/
function wrap( src, filename, dirname ) {
	var out = '';
	out += '(function(exports, require, module, __filename, __dirname){';
	out += '\n\n';
	out += src;
	out += '\n\n';
	out += '})(exports, require, module, "'+filename+'", "'+dirname+'");';
	return out;
}


// EXPORTS //

module.exports = wrap;
